import axios from 'axios';

// Criando instância personalizada do axios
const api = axios.create({
  baseURL: 'http://localhost:8080/api/fans', // Certifique-se de que o servidor está rodando nessa URL
  timeout: 5000,  // Timeout de 5 segundos para evitar travamentos
  headers: {
    'Content-Type': 'application/json',
    // Se precisar de autenticação, adicione o token aqui
    // 'Authorization': `Bearer ${token}`
  },
});

// Interceptador de requisições para adicionar tokens ou tratamento de erro global
api.interceptors.request.use(
  (config) => {
    // Se necessário, pode adicionar tokens de autenticação aqui
    // config.headers['Authorization'] = `Bearer ${token}`;
    return config;
  },
  (error) => {
    // Tratar erros antes de enviá-los
    return Promise.reject(error);
  }
);

// Interceptador de respostas para tratar erro globalmente
api.interceptors.response.use(
  (response) => {
    return response;  // Retorna a resposta da requisição
  },
  (error) => {
    if (error.response) {
      // Resposta recebida do servidor com erro
      console.error('Erro de resposta:', error.response);
    } else if (error.request) {
      // A requisição foi feita mas não houve resposta
      console.error('Erro na requisição:', error.request);
    } else {
      // Algo aconteceu ao configurar a requisição
      console.error('Erro geral:', error.message);
    }
    return Promise.reject(error);  // Lança o erro para ser tratado no componente
  }
);

export default api;
