import React, { useState } from 'react';
import api from '../api';

const CadastroFan = () => {
  const [fan, setFan] = useState({
    nome: '',
    email: '',
    cpf: '',
    interesses: '',
    endereco: ''
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Função para validar o CPF (simples)
  const validarCPF = (cpf) => {
    // Validação simples: CPF deve ter 11 dígitos
    return cpf.length === 11;
  };

  // Função para atualizar os campos do estado
  const handleChange = (e) => {
    setFan({ ...fan, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validação simples de CPF
    if (!validarCPF(fan.cpf)) {
      alert("CPF inválido.");
      return;
    }

    setLoading(true);  // Começa o carregamento
    setError(null);  // Reseta erro

    try {
      const response = await api.post('/fans', fan); // Verifique se o endpoint está correto
      alert("Fã cadastrado com sucesso! ID: " + response.data.id);
      setFan({
        nome: '',
        email: '',
        cpf: '',
        interesses: '',
        endereco: ''
      }); // Limpa os campos após o cadastro
    } catch (error) {
      console.error("Erro ao cadastrar fã:", error);
      setError("Erro ao cadastrar fã. Verifique o console para mais detalhes.");
    } finally {
      setLoading(false);  // Fim do carregamento
    }
  };

  return (
    <div>
      <h2>Cadastro de Fã</h2>
      <form onSubmit={handleSubmit}>
        <input
          name="nome"
          placeholder="Nome"
          onChange={handleChange}
          value={fan.nome}
          required
        />
        <input
          name="email"
          placeholder="Email"
          onChange={handleChange}
          value={fan.email}
          required
        />
        <input
          name="cpf"
          placeholder="CPF"
          onChange={handleChange}
          value={fan.cpf}
          required
        />
        <input
          name="interesses"
          placeholder="Interesses"
          onChange={handleChange}
          value={fan.interesses}
          required
        />
        <input
          name="endereco"
          placeholder="Endereço"
          onChange={handleChange}
          value={fan.endereco}
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Cadastrando...' : 'Cadastrar'}
        </button>
      </form>

      {error && <p style={{ color: 'red' }}>{error}</p>} {/* Exibe a mensagem de erro, se houver */}
    </div>
  );
};

export default CadastroFan;
