import React, { useState } from 'react';
import api from '../api';

const CadastroFan = () => {
  const [fan, setFan] = useState({
    nome: '',
    email: '',
    cpf: '',
    interesses: '',
    endereco: ''
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const validarCPF = (cpf) => {
    return cpf.length === 11;
  };

  const handleChange = (e) => {
    setFan({ ...fan, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validarCPF(fan.cpf)) {
      alert("CPF inválido.");
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await api.post('/api/fans', fan); // Certifique-se de que a URL está correta
      alert("Fã cadastrado com sucesso! ID: " + response.data.id);
      setFan({
        nome: '',
        email: '',
        cpf: '',
        interesses: '',
        endereco: ''
      });
    } catch (error) {
      console.error("Erro ao cadastrar fã:", error);
      setError(error.response?.data?.message || "Erro ao cadastrar fã. Tente novamente mais tarde.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Cadastro de Fã</h2>
      <form onSubmit={handleSubmit}>
        <input
          name="nome"
          placeholder="Nome"
          onChange={handleChange}
          value={fan.nome}
          required
        />
        <input
          name="email"
          placeholder="Email"
          onChange={handleChange}
          value={fan.email}
          required
        />
        <input
          name="cpf"
          placeholder="CPF"
          onChange={handleChange}
          value={fan.cpf}
          required
        />
        <input
          name="interesses"
          placeholder="Interesses"
          onChange={handleChange}
          value={fan.interesses}
          required
        />
        <input
          name="endereco"
          placeholder="Endereço"
          onChange={handleChange}
          value={fan.endereco}
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Cadastrando...' : 'Cadastrar'}
        </button>
      </form>

      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default CadastroFan;
